mapper.select:
SELECT '0' AS REQ_DEPT_CD , 'SEL' AS PRDLN_CD , '선택' AS PRDLN_NM , 'Y' AS USE_YN , '' AS EMP_NO , TO_DATE('') AS REG_DT FROM DUAL UNION ALL SELECT '0' AS REQ_DEPT_CD , 'ALL' AS PRDLN_CD , '전체' AS PRDLN_NM , 'Y' AS USE_YN , '' AS EMP_NO , TO_DATE('') AS REG_DT FROM DUAL UNION ALL SELECT REQ_DEPT_CD , PRDLN_CD , PRDLN_NM , USE_YN , EMP_NO , REG_DT FROM TM_PRDLN_MNG WHERE 1=1
AND REQ_DEPT_CD = #{req_dept_cd}
AND USE_YN = #{use_yn}
