mapper.insert:
		MERGE INTO
		    TM_ANLYS_STD A
		USING DUAL
		ON(
		        A.REQ_DEPT_CD = #{req_dept_cd}
		    AND A.PRDLN_CD = #{prdln_cd}
		    AND (A.KWD_NM = #{kwd_nm}
		    
	             OR A.KWD_NM = TRIM(REGEXP_SUBSTR(#{kwd_nm}, '[^/]+', 1, 2)) || '/' || TRIM(REGEXP_SUBSTR(#{kwd_nm}, '[^/]+', 1, 1))
            
	            )
		)
		WHEN NOT MATCHED THEN
		    INSERT(
		          REQ_DEPT_CD
		        , PRDLN_CD
		        , KWD_SPR
		        , SCRNG_SPR
		        , KWD_NM
		        , USE_YN
		        , EMP_NO
		        , REG_DT
		    )VALUES(
		          #{req_dept_cd}
		        , #{prdln_cd}
		        , #{kwd_spr}
		        , #{scrng_spr}
		        , #{kwd_nm}
		        , 'N'
		        , #{emp_no}
		        , SYSDATE
		    )

mapper.select:
		SELECT
			  KWD_NM
		FROM TM_ANLYS_STD
		WHERE
			  REQ_DEPT_CD = #{req_dept_cd}
		  AND PRDLN_CD = #{prdln_cd}
		  AND (KWD_NM = #{kwd_nm}
		  
          OR KWD_NM = TRIM(REGEXP_SUBSTR(#{kwd_nm}, '[^/]+', 1, 2)) || '/' || TRIM(REGEXP_SUBSTR(#{kwd_nm}, '[^/]+', 1, 1))
          
			  )

mapper.update:
		UPDATE
		    TM_ANLYS_STD
		SET
      		  SYN_NM = DECODE(#{chk_sel}, '0', '', '1', #{syn_nm})
		    , REG_DT = SYSDATE
		    , EMP_NO = #{emp_no}
		WHERE
				REQ_DEPT_CD = #{req_dept_cd}
		  	AND PRDLN_CD = #{prdln_cd}
		  	AND KWD_SPR = #{kwd_spr}
		  	AND SCRNG_SPR = #{scrng_spr}
		  	AND KWD_NM = #{kwd_nm}
		  	
	  		AND SYN_NM = #{org_syn_nm}
