mapper.select:
WITH FLUPL_INF AS ( SELECT * FROM TM_FLUPL_INF WHERE UPL_SPR='1' AND TRNS_STTS IN ('0', '1', '2')
AND CLS_CD=#{cls_cd}
AND REQ_DEPT_CD=#{req_dept_cd}
AND FIN_CD=#{fin_cd}
) SELECT '0' AS CHK , CLS_CD , REQ_DEPT_CD , FIN_CD , UP_FIN_CD , ORG_FIN_CD , (SELECT FINANCE_NAME FROM RR_FINANCE WHERE FINANCE_CD=T.FIN_CD) AS FIN_NM , REQ_DT , SAVE_FILE_NM , NODE_NM , TRNS_STTS , (SELECT CD_NM FROM TM_CD_INF WHERE GRP_CD='AnlySt' AND CD=T.TRNS_STTS) AS TRNS_STTS_NM , LEVEL AS LV , DECODE(LEVEL, 1, 0, 2) AS COL_IMG_IDX , DECODE(LEVEL, 1, 1, 3) AS EXPN_IMG_IDX , CONNECT_BY_ISLEAF AS IS_LEAF FROM ( SELECT DISTINCT CLS_CD , REQ_DEPT_CD , '0' AS UP_FIN_CD , REQ_DEPT_CD||FIN_CD AS FIN_CD , FIN_CD AS ORG_FIN_CD , NULL AS REQ_DT , NULL AS SAVE_FILE_NM , (SELECT FINANCE_NAME FROM RR_FINANCE WHERE FINANCE_CD=A.FIN_CD) AS NODE_NM , NULL AS TRNS_STTS , NULL AS REG_DT FROM FLUPL_INF A UNION ALL SELECT DISTINCT CLS_CD , REQ_DEPT_CD , REQ_DEPT_CD||FIN_CD AS UP_FIN_CD , REQ_DEPT_CD||FIN_CD||REQ_DT AS FIN_CD , FIN_CD AS ORG_FIN_CD , REQ_DT , NULL AS SAVE_FILE_NM , REQ_DT AS NODE_NM , NULL AS TRNS_STTS , NULL AS REG_DT FROM FLUPL_INF A UNION ALL SELECT CLS_CD , REQ_DEPT_CD , REQ_DEPT_CD||FIN_CD||REQ_DT AS UP_FIN_CD , FIN_CD , FIN_CD AS ORG_FIN_CD , REQ_DT , SAVE_FILE_NM , UPL_FILE_NM AS NODE_NM , TRNS_STTS , REG_DT FROM FLUPL_INF A ) T START WITH UP_FIN_CD='0' CONNECT BY PRIOR FIN_CD=UP_FIN_CD ORDER SIBLINGS BY REQ_DT DESC, REG_DT DESC
