mapper.select:
WITH FLUPL_INF AS ( SELECT * FROM TM_FLUPL_INF WHERE UPL_SPR='1' AND TRNS_STTS IN ('0', '1', '2')
AND CLS_CD=#{cls_cd}
AND REQ_DEPT_CD=#{req_dept_cd}
AND FIN_CD=#{fin_cd}
) SELECT '0' AS CHK , CLS_CD , REQ_DEPT_CD , FIN_CD , UP_FIN_CD , ORG_FIN_CD , (SELECT FINANCE_NAME FROM RR_FINANCE WHERE FINANCE_CD=T.FIN_CD) AS FIN_NM , REQ_DT , SAVE_FILE_NM , NODE_NM , TRNS_STTS , (SELECT CD_NM FROM TM_CD_INF WHERE GRP_CD='AnlySt' AND CD=T.TRNS_STTS) AS TRNS_STTS_NM , LEVEL AS LV , DECODE(LEVEL, 1, 0, 2) AS COL_IMG_IDX , DECODE(LEVEL, 1, 1, 3) AS EXPN_IMG_IDX , CONNECT_BY_ISLEAF AS IS_LEAF FROM ( SELECT DISTINCT CLS_CD , REQ_DEPT_CD , '0' AS UP_FIN_CD , REQ_DEPT_CD||FIN_CD AS FIN_CD , FIN_CD AS ORG_FIN_CD , NULL AS REQ_DT , NULL AS SAVE_FILE_NM , (SELECT FINANCE_NAME FROM RR_FINANCE WHERE FINANCE_CD=A.FIN_CD) AS NODE_NM , NULL AS TRNS_STTS , NULL AS REG_DT FROM FLUPL_INF A UNION ALL SELECT DISTINCT CLS_CD , REQ_DEPT_CD , REQ_DEPT_CD||FIN_CD AS UP_FIN_CD , REQ_DEPT_CD||FIN_CD||REQ_DT AS FIN_CD , FIN_CD AS ORG_FIN_CD , REQ_DT , NULL AS SAVE_FILE_NM , REQ_DT AS NODE_NM , NULL AS TRNS_STTS , NULL AS REG_DT FROM FLUPL_INF A UNION ALL SELECT CLS_CD , REQ_DEPT_CD , REQ_DEPT_CD||FIN_CD||REQ_DT AS UP_FIN_CD , FIN_CD , FIN_CD AS ORG_FIN_CD , REQ_DT , SAVE_FILE_NM , UPL_FILE_NM AS NODE_NM , TRNS_STTS , REG_DT FROM FLUPL_INF A ) T START WITH UP_FIN_CD='0' CONNECT BY PRIOR FIN_CD=UP_FIN_CD ORDER SIBLINGS BY REQ_DT DESC, REG_DT DESC

SELECT * FROM ( SELECT T1.* , T2.SAVE_FILE_NM , T2.TRNS_STTS FROM ( SELECT DISTINCT A.CLS_CD , A.REQ_DEPT_CD , A.REQ_DT , A.FIN_CD , (SELECT FINANCE_NAME FROM RR_FINANCE WHERE FINANCE_CD=A.FIN_CD) AS FIN_NM , A.SCRTS_NO , A.PRDLN_CD , (SELECT PRDLN_NM FROM TM_PRDLN_MNG WHERE USE_YN='Y' AND CLS_CD=A.CLS_CD AND REQ_DEPT_CD=A.REQ_DEPT_CD AND PRDLN_CD=A.PRDLN_CD) AS PRDLN_NM , SCR , B.INSPC_SPR , A.PRD_NM , A.CTT_DT , A.CTT_STTS , A.CTTOR_NM , '0' AS CHK_SEL FROM RR_TM001 A, TM_INSPC_RSLT B WHERE A.CLS_CD = B.CLS_CD(+) AND A.REQ_DEPT_CD = B.REQ_DEPT_CD(+) AND A.FIN_CD = B.FIN_CD (+) AND A.REQ_DT = B.REQ_DT(+) AND A.PRDLN_CD = B.PRDLN_CD(+) AND A.SCRTS_NO = B.SCRTS_NO(+) AND A.CLS_CD = #{cls_cd} AND A.REQ_DEPT_CD = #{req_dept_cd} AND A.PRDLN_CD = #{prdln_cd} AND A.FIN_CD = #{fin_cd} AND A.REQ_DT = #{req_dt} ) T1, ( SELECT DISTINCT B.CLS_CD , B.REQ_DEPT_CD , B.FIN_CD , B.REQ_DT , B.PRDLN_CD , B.SCRTS_NO , MAX(A.SAVE_FILE_NM) AS SAVE_FILE_NM , MAX(B.TRNS_STTS) KEEP(DENSE_RANK FIRST ORDER BY A.SAVE_FILE_NM DESC) AS TRNS_STTS FROM TM_FLUPL_INF A, TM_RCDFL_INF B WHERE A.CLS_CD=B.CLS_CD AND A.REQ_DEPT_CD=B.REQ_DEPT_CD AND A.REQ_DT=B.REQ_DT AND A.FIN_CD=B.FIN_CD AND A.SAVE_FILE_NM=B.SAVE_FILE_NM AND A.UPL_SPR = '1' AND B.USE_YN = 'Y' AND A.CLS_CD = #{cls_cd} AND A.REQ_DEPT_CD = #{req_dept_cd} AND A.REQ_DT = #{req_dt} AND A.FIN_CD = #{fin_cd} AND
A.SAVE_FILE_NM = #{fList.save_file_nm}
GROUP BY B.CLS_CD, B.REQ_DEPT_CD, B.FIN_CD, B.REQ_DT, B.PRDLN_CD, B.SCRTS_NO ) T2 WHERE T1.CLS_CD = T2.CLS_CD AND T1.REQ_DEPT_CD = T2.REQ_DEPT_CD AND T1.FIN_CD = T2.FIN_CD AND T1.REQ_DT = T2.REQ_DT AND T1.PRDLN_CD = T2.PRDLN_CD AND T1.SCRTS_NO = T2.SCRTS_NO
AND T1.SCRTS_NO like '%'||#{scrts_no}||'%'
) PIVOT(SUM(SCR) AS SCR FOR INSPC_SPR IN ('1' AS AUTO, '2' AS MANUAL)) ORDER BY PRDLN_NM ASC, PRDLN_CD ASC
